import { useState } from "react";

function AdPage() {
  const [advertisement, setAdvertisement] = useState(true);
  const toggle = () => {
    setAdvertisement(!advertisement);
  };
  return (
    <div>
      <button onClick={toggle}>
            {advertisement ? "광고 안보기" : "광고 보기"}
        </button>;

    </div>
  );
}

export default AdPage;
