import styled from "styled-components";

export const Container = styled.div`
    width: 900px;
    height: 600px;
    background-color: #fafafa;
    margin-top: 48px;
    margin-left: auto;
    margin-right: auto;
    padding: 20px;
`;

export const Title = styled.h1`
    font-size: 36px;
    margin-bottom: 52px;
`;

export const InputText = styled.p`
    font-size: 16px;
    margin-bottom: 12px;

`;

export const InputBox = styled.input`
    width: 700px;
    height: 60px;
    border-radius: 12px;
`;
