import styled from "styled-components";

export const Container = styled.div`
    width: 900px;
    height: 600px;
    background-color: #fafafa;
    margin-top: 48px;
    margin-left: auto;
    margin-right: auto;
    padding: 20px;
`;

export const Title = styled.h1`
    font-size: 36px;
`;

export const InputText = styled.p`
    font-size: 16px;

`;
